package com.tanvirhossen.dollarbucks.view;

import androidx.annotation.NonNull;
import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import android.graphics.BitmapFactory;
import android.os.Bundle;
import android.util.Base64;
import android.view.View;
import android.widget.ImageButton;
import android.widget.ImageView;
import android.widget.ProgressBar;

import com.google.android.gms.tasks.OnCompleteListener;
import com.google.android.gms.tasks.Task;
import com.google.firebase.firestore.DocumentSnapshot;
import com.google.firebase.firestore.FirebaseFirestore;
import com.google.firebase.firestore.QueryDocumentSnapshot;
import com.google.firebase.firestore.QuerySnapshot;
import com.orhanobut.logger.AndroidLogAdapter;
import com.orhanobut.logger.Logger;
import com.tanvirhossen.dollarbucks.R;
import com.tanvirhossen.dollarbucks.adapter.ProofAdapter;

import java.util.ArrayList;
import java.util.Map;

public class ProofActivity extends AppCompatActivity {
    private ProofAdapter proofAdapter;
    private RecyclerView recyclerView;
    private ArrayList<String> proofImageList;
    private ProgressBar progressBar;
    private FirebaseFirestore firebaseFirestore;
    private ImageButton imageButtonBack;
    private ImageView imageViewDemo;
    private String demoImage = "";

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_proof);
        findViewById();
        setRecyclerView();
        //setExistImage(imageViewDemo, demoImage);
    }

    private void findViewById() {
        firebaseFirestore = FirebaseFirestore.getInstance();
        Logger.addLogAdapter(new AndroidLogAdapter());
        imageButtonBack = findViewById(R.id.proof_back);
        recyclerView = findViewById(R.id.proof_recyclerview);
        progressBar = findViewById(R.id.proof_progress);
        imageViewDemo = findViewById(R.id.demo_image_check);
    }

    private void setRecyclerView() {
        progressBar.setVisibility(View.VISIBLE);
        firebaseFirestore.collection("proof").get().addOnCompleteListener(new OnCompleteListener<QuerySnapshot>() {
            @Override
            public void onComplete(@NonNull Task<QuerySnapshot> task) {
                proofImageList = new ArrayList<>();
                progressBar.setVisibility(View.GONE);
                if (task.isSuccessful()) {
                    for (QueryDocumentSnapshot queryDocumentSnapshot: task.getResult()) {
                        proofImageList.add(queryDocumentSnapshot.get("image").toString());
                    }
                    proofAdapter = new ProofAdapter(proofImageList, ProofActivity.this);
                    recyclerView.setLayoutManager(new LinearLayoutManager(ProofActivity.this));
                    recyclerView.setHasFixedSize(true);
                    recyclerView.setAdapter(proofAdapter);
                }
            }
        });

    }

    private void setExistImage(ImageView imageView, String base64String) {
        if (!base64String.isEmpty()) {
            byte[] bytes = Base64.decode(base64String, Base64.DEFAULT);
            imageView.setImageBitmap(BitmapFactory.decodeByteArray(bytes, 0, bytes.length));
        }
    }
}